
var ComponentsBootstrapSelect = function() {
	var n = function() {
		$(".bs-select").selectpicker({
			iconBase : "fa",
			tickIcon : "fa-check"
		})
	};
	return {
		init : function() {
			n()
		}
	}
}();

var ComponentsFixFilter = function() {
	var n = function() {
		var $filter = $("#filter");
		
		$('.filter-btn').on('click', function() {
			var $this = $(this);
			var $filter = $this.parent('#filter');
			if($filter.hasClass('fixed-filter')) {
				$filter.removeClass('fixed-filter');			
				$filter.udraggable('destroy');
				$filter.css({position:'relative',top:0,left:'auto',right:0});
			}else {
				$filter.addClass('fixed-filter');
				$filter.udraggable();
			}
		});		
	};
	return {
		init : function() {
			n()
		}
	}
}();

jQuery(document).ready(function() {
	ComponentsBootstrapSelect.init();
	ComponentsFixFilter.init();
});
function splitPageLink(formId, toPage) {
	$(formId + " select[name=pageNumber],input[name=pageNumber] ").val(toPage);
	$('#' + formId).submit();
}